package com.cifpay.insurance.param.vendor;

import java.io.Serializable;

/**
 * 查找银行卡结果
 * 
 * @author 张均锋
 *
 */
public class LookupBankCardResult implements Serializable {

	/**
	 * 
	 */
	private static final long serialVersionUID = 1L;
	/** 卡号前缀 **/
	private String cardBin;
	/** 银行代码 **/
	private String bankCode;
	/** 银行名称 **/
	private String bankName;
	/** 卡类型 **/
	private String cardType;
	/** 卡号位数 **/
	private Integer cardDigits;

	public String getCardBin() {
		return cardBin;
	}

	public void setCardBin(String cardBin) {
		this.cardBin = cardBin;
	}

	public String getBankCode() {
		return bankCode;
	}

	public void setBankCode(String bankCode) {
		this.bankCode = bankCode;
	}

	public String getBankName() {
		return bankName;
	}

	public void setBankName(String bankName) {
		this.bankName = bankName;
	}

	public String getCardType() {
		return cardType;
	}

	public void setCardType(String cardType) {
		this.cardType = cardType;
	}

	public Integer getCardDigits() {
		return cardDigits;
	}

	public void setCardDigits(Integer cardDigits) {
		this.cardDigits = cardDigits;
	}

}
